site.headStyle:
.hero-scroll-down {
  cursor: pointer;
}

@media (min-width:480px) {
  body div.container-element.section-element.hero {
    height: 100vh;
  }
}

div.image .text-element {
  position: absolute;
  top: 20px;
  bottom: 20px;
  left: 20px;
  right: 20px;
  width: initial;
  height: initial;
}

.silex-runtime div.image .text-element {
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
}

@media (min-width:480px) {
  div.image .text-element {
    background-color: rgba(255, 255, 255, .6);
  }

  div.image .silex-element-content {
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 100%;
  }

  .silex-runtime div.image .text-element {
    opacity: 0;
    transition: opacity .3s ease;
  }

  .silex-runtime div.image:hover .text-element {
    opacity: 1;
  }
}

@media (min-width: 1260px) {
  body div.container-element.section-element.hero {
    border: 30px solid rgba(214, 214, 214, 0.857);
    box-sizing: border-box;
  }
}

h1 {
  font-family: 'MrsBeasleyRegular';
  font-weight: bold;
  font-style: normal;
}

p {
  font-family: 'FantasqueSansMonoRegular';
}

div.rotate {
  transform: rotate(04deg);
}

div.full-screen {
    width: 100%
}

div.center {
  position: absolute;
  left: 0%;
  right: 0%;
  margin: auto;
}

div.position-left {
    left: 10%;
    right: 0%;
}

div.position-right {
    left: 0%;
    right: -50%;
    margin: auto
}

@media (min-width:481px) {
  body {
    overflow-x: hidden;
  }

  .silex-runtime .animation-element.slide-left {
    opacity: 0;
    -moz-transition: all 500ms ease-out;
    -webkit-transition: all 500ms ease-out;
    -o-transition: all 500ms ease-out;
    transition: all 500ms ease-out;
    -moz-transform: translate3d(-100px, 0px, 0px);
    -webkit-transform: translate3d(-100px, 0px, 0px);
    -o-transform: translate(-100px, 0px);
    -ms-transform: translate(-100px, 0px);
    transform: translate3d(-100px, 0px, 0px);
  }

  .silex-runtime .animation-element.slide-right {
    opacity: 0;
    -moz-transition: all 500ms ease-out;
    -webkit-transition: all 500ms ease-out;
    -o-transition: all 500ms ease-out;
    transition: all 500ms ease-out;
    -moz-transform: translate3d(100px, 0px, 0px);
    -webkit-transform: translate3d(100px, 0px, 0px);
    -o-transform: translate(100px, 0px);
    -ms-transform: translate(100px, 0px);
    transform: translate3d(100px, 0px, 0px);
  }

  .silex-runtime .animation-element.slide-fade {
    opacity: 0;
    -moz-transition: all 800ms ease-out;
    -webkit-transition: all 800ms ease-out;
    -o-transition: all 800ms ease-out;
    transition: all 800ms ease-out;
  }

  .silex-runtime .animation-element.slide-top1,
  .silex-runtime .animation-element.slide-top2,
  .silex-runtime .animation-element.slide-top3 {
    opacity: 0;
    -moz-transition: all 800ms ease-out;
    -webkit-transition: all 800ms ease-out;
    -o-transition: all 800ms ease-out;
    transition: all 800ms ease-out;
    -moz-transform: translate3d(0px, 100px, 0px);
    -webkit-transform: translate3d(0px, 100px, 0px);
    -o-transform: translate(0px, 100px);
    -ms-transform: translate(0px, 100px);
    transform: translate3d(0px, 100px, 0px);
  }

  .silex-runtime .animation-element.slide-top2 {
    -moz-transform: translate3d(0px, 200px, 0px);
    -webkit-transform: translate3d(0px, 200px, 0px);
    -o-transform: translate(0px, 200px);
    -ms-transform: translate(0px, 200px);
    transform: translate3d(0px, 200px, 0px);
  }

  .silex-runtime .animation-element.slide-top3 {
    -moz-transform: translate3d(0px, 300px, 0px);
    -webkit-transform: translate3d(0px, 300px, 0px);
    -o-transform: translate(0px, 300px);
    -ms-transform: translate(0px, 300px);
    transform: translate3d(0px, 300px, 0px);
  }

  .silex-runtime .animation-element.in-view {
    opacity: 1;
    -moz-transform: translate3d(0px, 0px, 0px);
    -webkit-transform: translate3d(0px, 0px, 0px);
    -o-transform: translate(0px, 0px);
    -ms-transform: translate(0px, 0px);
    transform: translate3d(0px, 0px, 0px);
  }
}

site.headScript:
$(function() {
    $('.hero-scroll-down').click(function() {
        $([document.documentElement, document.body]).animate({
            scrollTop: $("#after-hero").offset().top
        }, 500);
    });
})



/* animation on scroll widget */
$(function() {
    var $animation_elements = $('.animation-element');
    var $window = $(window);

    function check_if_in_view() {
    var window_height = $window.height();
    var window_top_position = $window.scrollTop();
    var window_bottom_position = (window_top_position + window_height);

    $.each($animation_elements, function() {
        var $element = $(this);
        var element_height = $element.outerHeight();
        var element_top_position = $element.offset().top;
        var element_bottom_position = (element_top_position + element_height);

        //check to see if this current container is within viewport
        if ((element_bottom_position >= window_top_position) &&
            (element_top_position <= window_bottom_position)) {
        $element.addClass('in-view');
        } else {
        $element.removeClass('in-view');
        }
    });
    }

    $window.on('scroll resize', check_if_in_view);
    $window.trigger('scroll');

})
/* /animation on scroll widget */
